import React, { useState, useEffect } from "react";
import App from "../App";
const PokemonTypeFilter = (props) => {
  const [filtered, setFiltered] = useState("");
  const [pokemon, setPokemon] = useState([]);
  const [isValid, setIsValid] = useState(true);
  console.log(props.pokemonTypes);
  const pokemonInputHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setFiltered(event.target.value);
    // setPokemon(props.pokemonTypes);
  };
  const typeFilterHandler = (event) => {
    event.preventDefault();
    console.log(filtered);
    const filteredPokemon = props.pokemonTypes.filter(
      (type) => type.name === filtered
    );
    console.log(filteredPokemon);
  };
  return (
    <div>
      Pokemon filter
      <form onSubmit={typeFilterHandler}>
        <input type="text" onChange={pokemonInputHandler}></input>
        <button type="subtim">Filter Pok√©mon by type</button>
      </form>
    </div>
  );
};

export default PokemonTypeFilter;
