import React, { useState, useEffect, useCallback } from "react";

import MoviesList from "./components/MoviesList";

import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://pokeapi.co/api/v2/type/");
      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();

      const transformedMovies = data.results.map(async (movieData) => {
        const eachResponse = await fetch(movieData.url)
          .then((response) => {
            return response.json();
          })

          .then((abilitiesObject) => {
            let weaknesess = [];
            let strenghts = [];
            let nameCategory = abilitiesObject.name;

            for (let item of abilitiesObject.damage_relations.no_damage_from) {
              strenghts.push(item.name);
            }
            for (let item of abilitiesObject.damage_relations
              .half_damage_from) {
              strenghts.push(item.name);
            }
            for (let item of abilitiesObject.damage_relations
              .double_damage_to) {
              strenghts.push(item.name);
            }
            for (let item of abilitiesObject.damage_relations.no_damage_to) {
              weaknesess.push(item.name);
            }
            for (let item of abilitiesObject.damage_relations.half_damage_to) {
              weaknesess.push(item.name);
            }
            for (let item of abilitiesObject.damage_relations
              .double_damage_from) {
              weaknesess.push(item.name);
            }
            // const weaknessesSet = new Intl.ListFormat().format([
            //   ...new Set(weaknesess),
            // ]);
            // const strenghtsSet = new Intl.ListFormat().format([
            //   ...new Set(strenghts),
            // ]);
            const weaknessesSet = new Set(weaknesess)
              .reduce(function (prev, curr, i) {
                return prev + curr + (i === arr.length - 2 ? " and " : ", ");
              }, "")
              .slice(0, -2);
            const strenghtsSet = new Intl.ListFormat().format([
              ...new Set(strenghts),
            ]);
            // console.log(
            //   `${nameCategory} fuerte contra: ${strenghtsSet}---------debil contra: ${weaknessesSet}`
            // );
            return {
              name: nameCategory,
              strongAgainst: strenghtsSet,
              worstAgainst: weaknessesSet,
            };
          });
        console.log(eachResponse);
        setMovies((prevArray) => [...prevArray, eachResponse]);
        return {
          name: movieData.name,
          strongAgainst: [await eachResponse.betterTo],
          worstAgainst: [await eachResponse.worstTo],
        };
      });
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  function addMovieHandler(movie) {
    console.log(movie);
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Pokemon Classes</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
