import React, { useState, useEffect } from "react";
import App from "../App";
import MoviesList from "../components/MoviesList";
import classes from "./PokemonTypeFilter.module.css";
const PokemonTypeFilter = (props) => {
  const [filtered, setFiltered] = useState("");
  const [pokemon, setPokemon] = useState([]);
  const [isValid, setIsValid] = useState(null);
  const [isFiltered, setIsFiltered] = useState(null);
  const [isTouched, setIsTouched] = useState(false);
  // console.log(props.pokemonTypes);

  const pokemonInputHandler = (event) => {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
      setIsTouched(true);
    }
    if (event.target.value.trim().length === 0) {
      setIsValid(false);
      setIsTouched(false);
    }
    setFiltered(event.target.value);
  };
  console.log(isValid);

  const inputBlurHandler = (event) => {
    console.log("onBlur fired");
    setIsTouched(true);
  };

  const typeFilterHandler = (event) => {
    event.preventDefault();

    const filteredPokemon = props.pokemonTypes.filter(
      (type) => type.name === filtered.toLowerCase().trim("")
    );
    if (filteredPokemon.length > 0) {
      setIsFiltered(true);
    }
    if (filteredPokemon.length === 0) {
      setIsFiltered(false);
    }
    console.log(filteredPokemon);
    return setPokemon(filteredPokemon);
  };

  let content = (
    <div>
      Pokemon filter
      <form onSubmit={typeFilterHandler}>
        <input
          type="text"
          onChange={pokemonInputHandler}
          onBlur={inputBlurHandler}
          placeholder="Enter a Pokémon type"
        ></input>
        <button type="submit">Filter Pokémon by type</button>
      </form>
      {isValid && isFiltered && (
        <h3>
          {" "}
          Your filtered Pokémon type: <MoviesList movies={pokemon} />{" "}
        </h3>
      )}
    </div>
  );
  if (isFiltered === false && pokemon.length < 0) {
    content = (
      <div>
        Pokemon filter
        <form onSubmit={typeFilterHandler}>
          <input
            type="text"
            onChange={pokemonInputHandler}
            onBlur={inputBlurHandler}
          ></input>
          <button type="submit">Filter Pokémon by type</button>
        </form>
        <h4> The requested Pokémon type couldn't be found, try again! </h4>
      </div>
    );
  }
  if (isValid === false || isTouched === true) {
    content = (
      <div>
        Pokemon filter
        <form onSubmit={typeFilterHandler}>
          <input
            type="text"
            onChange={pokemonInputHandler}
            on
            onBlur={inputBlurHandler}
          ></input>
          <button type="submit" disabled={isValid != true}>
            Filter Pokémon by type
          </button>
        </form>
        <h4> Please enter a Pokémon type to search </h4>
      </div>
    );
  }

  //  (isValid && isFiltered) {
  //     content = (
  //       <ul className={classes["types-list"]}>
  //         <Movie
  //           key="1"
  //           name={pokemon.name}
  //           strongTo={pokemon.strongAgainst}
  //           worstAgainst={pokemon.worstAgainst}
  //         />
  //       </ul>
  //     );
  //   }
  // if (isValid && isFiltered) {
  //   content = (
  //     <div>
  //       <Movie
  //         key={matchingType.name}
  //         name={matchingType.name}
  //         strongTo={matchingType.strongAgainst}
  //         worstAgainst={matchingType.worstAgainst}
  //       />
  //     </div>
  //   );
  // }
  return <div>{content}</div>;
};

export default PokemonTypeFilter;
