        //   return {
        //     name: nameCategory,
        //     betterTo: strenghtsSet,
        //     worstTo: weaknessesSet,
        //   };
        // });

        return {
          name: movieData.name,
          strongAgainst: eachResponse.betterTo,
          worstAgainst: eachResponse.worstTo,
        };
      });

      setMovies(transformedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);

  function addMovieHandler(movie) {
    console.log(movie);
  }

  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>{/* <AddMovie onAddMovie={addMovieHandler} /> */}</section>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Pokemon Classes</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
